<template>
  <img class="ui avatar image" :src="base" alt="">
</template>

<script>

const baseImg = 'data:image/jpeg;base64,'

export default {
  name: 'room-avatar',

  props: ['url', 'contact', 'show'],

  data () {
    return {
      base: baseImg
    }
  },

  mounted () {
    const getImg = require('electron').remote.getGlobal('getImg')
    if (this.contact && this.show) {
      getImg(this.contact, res => {
        if (res) {
          this.base = res
        } else {
          this.base = baseImg
        }
      })
    }
  }
}
</script>

<style scoped>
img {
  width: 40px;
  height: 40px;
  border-radius: 20px;
  overflow-y: hidden;
}
</style>
